        XCTAssertEqual("", try ABI.encodeParameter("bytes[]", ["abc".data(using: .utf8)!, "def".data(using: .utf8)!]))
        
        XCTAssertEqual("", try ABI.encodeParameter("string[2][2]", [["aaa", "bbb"], ["aaa", "bbb"]]))
        
        XCTAssertEqual("", try ABI.encodeParameter("bytes[2][2]", [["abc".data(using: .utf8)!, "def".data(using: .utf8)!], ["abc".data(using: .utf8)!, "def".data(using: .utf8)!]]))

        XCTAssertEqual("", try ABI.encodeParameter("string[][2]", [["aaa", "bbb"], ["aaa", "bbb"]]))

